mapper.select:
SELECT DISCT_NO,EVENT_NO, MARKET_NAME, EVENT_NAME, REGST_DATE, START_DATE,END_DATE,LIKE_CNT ,HIT_NUM ,RNUM FROM (SELECT D.DISCT_NO ,E.EVENT_NO, M.MARKET_NAME,E.EVENT_NAME,TO_CHAR(E.REGST_DATE,'YY-MM-DD') AS REGST_DATE, TO_CHAR(E.START_DATE,'YY-MM-DD') AS START_DATE,TO_CHAR(E.END_DATE,'YY-MM-DD') AS END_DATE, E.HIT_NUM, ROW_NUMBER() OVER(ORDER BY E.EVENT_NO DESC) AS RNUM, (SELECT COUNT(*) FROM EVENT_LIKE EL WHERE EL.EVENT_NO = E.EVENT_NO) AS LIKE_CNT FROM EVENT E JOIN MARKET_MEMBER MM ON( E.MARKET_MEMBER_NO = MM.MARKET_MEMBER_NO) JOIN MARKET M ON( MM.MARKET_NO = M.MARKET_NO) JOIN DISCT D ON( M.DISCT_NO = D.DISCT_NO) WHERE DEL_FLAG = 0
AND E.EVENT_NAME LIKE '%' || #{searchText} || '%'
AND M.MARKET_NAME LIKE '%' || #{searchText} || '%'
) E WHERE RNUM BETWEEN #{startCnt} AND #{endCnt} AND DISCT_NO = #{disctNo}

SELECT COUNT(*) AS QNT FROM EVENT E JOIN MARKET_MEMBER MM ON( E.MARKET_MEMBER_NO = MM.MARKET_MEMBER_NO) JOIN MARKET M ON( MM.MARKET_NO = M.MARKET_NO) JOIN DISCT D ON( M.DISCT_NO = D.DISCT_NO) WHERE DEL_FLAG = 0 AND D.DISCT_NO = #{disctNo}
AND E.EVENT_NAME LIKE '%' || #{searchText} || '%'
AND M.MARKET_NAME LIKE '%' || #{searchText} || '%'

SELECT DISCT_NO 
FROM DISCT
WHERE DISCT_NAME = #{disctName}

SELECT DISCT_NAME
FROM DISCT
WHERE DISCT_NO = #{disctNo}

SELECT COUNT(*)
FROM EVENT_LIKE
WHERE MEMBER_NO = #{memberNo}
AND EVENT_NO = #{eventNo}

SELECT COUNT(*)
FROM EVENT_LIKE
WHERE EVENT_NO = #{eventNo}

mapper.delete:
DELETE FROM EVENT_LIKE
WHERE MEMBER_NO = #{memberNo}
AND EVENT_NO = #{eventNo}
