mapper.select:
SELECT NOTICE_NO, NOTICE_TYPE,TITLE, CON,HIT_NUM,REGST_DATE FROM ( SELECT NOTICE_NO, NOTICE_TYPE,TITLE, CON,HIT_NUM,TO_CHAR(REGST_DATE, 'YY-MM-DD') AS REGST_DATE, ROW_NUMBER() OVER(ORDER BY NOTICE_NO DESC) AS RNUM FROM NOTICE WHERE DEL_FLAG = 0
AND TITLE LIKE '%' || #{searchText} || '%'
) WHERE RNUM BETWEEN #{startCnt} AND ${endCnt}
